import React, { useContext, useEffect, useState } from 'react';
import { AuthContext } from "../context/AuthContext";
import { parseISO, addDays, startOfDay, endOfDay, format } from 'date-fns';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import DraggableEvent from './DraggableEvent';
import DroppableDay from './DroppableDay';

const Home = () => {
    const { fetchCalendarEntries, addCalendarEntry, updateCalendarEntry, deleteCalendarEntry } = useContext(AuthContext);
    const [entries, setEntries] = useState([]);
    const [newEntry, setNewEntry] = useState({ title: '', description: '', date: '', category: '' });
    const [editingEntry, setEditingEntry] = useState(null);

    useEffect(() => {
        const getEntries = async () => {
            const fetchedEntries = await fetchCalendarEntries();
            console.log('Fetched Entries:', fetchedEntries);
            setEntries(fetchedEntries);
        };

        getEntries();
    }, [fetchCalendarEntries]);

    const today = startOfDay(new Date());
    const daysForward = 6;
    const endDate = endOfDay(addDays(today, daysForward));

    const filteredEntries = entries.filter(entry => {
        const entryDate = parseISO(entry.date);
        return entryDate >= today && entryDate <= endDate;
    });

    const generateWeekDays = () => {
        const weekDays = [];
        for (let i = 0; i <= daysForward; i++) {
            const day = addDays(today, i);
            weekDays.push({
                date: day,
                label: format(day, 'EEEE')
            });
        }
        return weekDays;
    };

    const weekDays = generateWeekDays();

    const groupedEntries = weekDays.map(day => ({
        ...day,
        entries: filteredEntries.filter(entry => {
            const entryDate = parseISO(entry.date);
            return entryDate.toDateString() === day.date.toDateString();
        })
    }));

    const handleDrop = async (item, newDate) => {
        const updatedEntry = { ...entries.find(entry => entry._id === item.id), date: newDate.toISOString() };
        await updateCalendarEntry(item.id, updatedEntry);
        setEntries(entries.map(entry => entry._id === item.id ? updatedEntry : entry));
    };

    return (
        <DndProvider backend={HTML5Backend}>
            <div>
                <div className="eventsContainer">
                    {groupedEntries.length > 0 ? groupedEntries.map(day => (
                        <DroppableDay
                            key={day.date.toISOString()}
                            date={day.date}
                            label={day.label}
                            entries={day.entries}
                            onDrop={handleDrop}
                        />
                    )) : (
                        <p>No events for the upcoming week.</p>
                    )}
                </div>
            </div>
        </DndProvider>
    );
};

export default Home;
