import React, { useState, useEffect } from 'react';
import { Pie } from 'react-chartjs-2';
import 'chart.js/auto';
import '../css/Chart.css';  // Adjusted import for CSS

const WorkoutSplit = ({ workoutData = [] }) => {
    const [timeframe, setTimeframe] = useState('Last week');
    const [chartData, setChartData] = useState({});
    const [lastWorkouts, setLastWorkouts] = useState([]);

    useEffect(() => {
        // Update chart data based on the selected timeframe
        const data = workoutData.map(workout => workout.percentage);
        const colors = workoutData.map(workout => workout.color);
        const labels = workoutData.map(workout => workout.category);

        setChartData({
            labels,
            datasets: [
                {
                    data,
                    backgroundColor: colors,
                },
            ],
        });

        setLastWorkouts(workoutData.map(workout => ({
            category: workout.category,
            lastWorkout: workout.lastWorkout,
        })));
    }, [timeframe, workoutData]);

    const renderNoDataMessage = () => {
        if (timeframe === 'Last week' && workoutData.every(workout => workout.percentage === 0)) {
            return 'No practices logged last week';
        }
        if (timeframe === 'Last month' && workoutData.every(workout => workout.percentage === 0)) {
            return 'No practices logged last month';
        }
        return null;
    };

    const renderLastWorkouts = () => {
        if (lastWorkouts.length === 0) {
            return <p>No practices have been logged yet</p>;
        }
        return (
            <div className="last-workouts">
                {lastWorkouts.map((workout, index) => (
                    <p key={index}>
                        {workout.category}: {workout.lastWorkout}
                    </p>
                ))}
            </div>
        );
    };

    return (
        <div className="workout-split">
            <h1>Workout Split</h1>
            <div className="timeframe-buttons">
                <button onClick={() => setTimeframe('Last month')}>Last month</button>
                <button onClick={() => setTimeframe('Last week')}>Last week</button>
                <button onClick={() => setTimeframe('Next week')}>Next week</button>
            </div>
            <div className="chart-container">
                {renderNoDataMessage() || (
                    <Pie data={chartData} />
                )}
            </div>
            <div className="last-workouts-container">
                {renderLastWorkouts()}
            </div>
        </div>
    );
};

export default WorkoutSplit;
