import React, { useState, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';

const Register = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [passwordsMatch, setPasswordsMatch] = useState(true);
    const { register } = useContext(AuthContext);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (password === confirmPassword) {
            await register(username, password);
        } else {
            setPasswordsMatch(false);
        }
    };

    const handlePasswordChange = (e) => {
        setPassword(e.target.value);
        if (confirmPassword !== e.target.value) {
            setPasswordsMatch(false);
        } else {
            setPasswordsMatch(true);
        }
    };

    const handleConfirmPasswordChange = (e) => {
        setConfirmPassword(e.target.value);
        if (password !== e.target.value) {
            setPasswordsMatch(false);
        } else {
            setPasswordsMatch(true);
        }
    };

    return (
        <div id="loginContainer">
            <div id="loginHeader">Sign Up</div>
            <div id="formContainer">
                <form onSubmit={handleSubmit}>
                    <div className="inputGroup">
                        <label id="inputHeading" htmlFor="usernameInput">Email</label>
                        <input
                            id="usernameInput"
                            type="text"
                            placeholder="example@email.com"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                        />
                    </div>
                    <div className="inputGroup">
                        <label id="inputHeading" htmlFor="passwordInput">Password</label>
                        <input
                            id="passwordInput"
                            type="password"
                            placeholder="Password"
                            value={password}
                            onChange={handlePasswordChange}
                        />
                    </div>
                    <div className="inputGroup">
                        <label id="inputHeading" htmlFor="confirmPasswordInput">Confirm password</label>
                        <input
                            id="confirmPasswordInput"
                            type="password"
                            placeholder="Confirm password"
                            value={confirmPassword}
                            onChange={handleConfirmPasswordChange}
                            style={{ borderColor: passwordsMatch ? '' : 'red' }}
                        />
                    </div>
                    <button type="submit" id="loginButton" disabled={!passwordsMatch}>Register</button>
                </form>
            </div>
            <div>
                <a id="registerLink" href="/login">Already have an account? Log in</a>
            </div>
        </div>
    );
};

export default Register;
