import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';
import { decodeToken } from '../utils/decodeToken';

const AuthContext = createContext();

const axiosInstance = axios.create({
    baseURL: 'http://localhost:5000', // Set your base URL here
});

const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            axiosInstance.defaults.headers.common['Authorization'] = `Bearer ${token}`;
            try {
                const decodedToken = decodeToken(token);
                setUser({ username: decodedToken.username });
            } catch (error) {
                console.error('Invalid token:', error);
                logout();
            }
        }
        setLoading(false);
    }, []);

    const login = async (username, password) => {
        try {
            const response = await axiosInstance.post('/api/auth/login', { username, password });
            const token = response.data.token;
            localStorage.setItem('token', token);
            axiosInstance.defaults.headers.common['Authorization'] = `Bearer ${token}`;
            const decodedToken = decodeToken(token);
            setUser({ username: decodedToken.username });
        } catch (error) {
            console.error('Login error:', error);
            throw error;
        }
    };

    const register = async (username, password) => {
        try {
            await axiosInstance.post('/api/auth/register', { username, password });
        } catch (error) {
            console.error('Registration error:', error);
            throw error;
        }
    };

    const logout = () => {
        localStorage.removeItem('token');
        delete axiosInstance.defaults.headers.common['Authorization'];
        setUser(null);
    };

    const fetchCalendarEntries = async () => {
        if (!axiosInstance.defaults.headers.common['Authorization']) {
            const token = localStorage.getItem('token');
            if (token) {
                axiosInstance.defaults.headers.common['Authorization'] = `Bearer ${token}`;
            } else {
                throw new Error('No token, authorization denied');
            }
        }
        try {
            const response = await axiosInstance.get('/api/calendar/entries');
            return response.data;
        } catch (error) {
            if (error.message.includes('blocked by CORS policy')) {
                console.warn('Request blocked by CORS policy, ignoring:', error);
                return []; // Return an empty array or a default value
            } else if (error.response && error.response.status === 401) {
                console.error('Authorization error:', error.response);
                // Handle unauthorized access
                return [];
            } else {
                console.error('Error fetching calendar entries:', error);
                throw error;
            }
        }
    };

    const addCalendarEntry = async (entry) => {
        try {
            const response = await axiosInstance.post('/api/calendar/entries', entry);
            return response.data;
        } catch (error) {
            if (error.message.includes('blocked by CORS policy')) {
                console.warn('Request blocked by CORS policy, ignoring:', error);
                return null; // Return a default value or null
            } else {
                console.error('Error adding calendar entry:', error);
                throw error;
            }
        }
    };

    const updateCalendarEntry = async (id, entry) => {
        try {
            const response = await axiosInstance.put(`/api/calendar/entries/${id}`, entry);
            return response.data;
        } catch (error) {
            if (error.message.includes('blocked by CORS policy')) {
                console.warn('Request blocked by CORS policy, ignoring:', error);
                return null; // Return a default value or null
            } else {
                console.error('Error updating calendar entry:', error);
                throw error;
            }
        }
    };

    const deleteCalendarEntry = async (id) => {
        try {
            const response = await axiosInstance.delete(`/api/calendar/entries/${id}`);
            return response.data;
        } catch (error) {
            if (error.message.includes('blocked by CORS policy')) {
                console.warn('Request blocked by CORS policy, ignoring:', error);
                return null; // Return a default value or null
            } else {
                console.error('Error deleting calendar entry:', error);
                throw error;
            }
        }
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <AuthContext.Provider value={{ user, login, register, logout, fetchCalendarEntries, addCalendarEntry, updateCalendarEntry, deleteCalendarEntry }}>
            {children}
        </AuthContext.Provider>
    );
};

export { AuthContext, AuthProvider };
