import React from 'react';
import { Pie } from 'react-chartjs-2';
import './Chart.css';

const data = {
    labels: ['Basketball', 'Strength', 'Recovery'],
    datasets: [
        {
            data: [50, 25, 25],
            backgroundColor: ['#FF8C00', '#87CEEB', '#9370DB'],
            hoverBackgroundColor: ['#FF8C00', '#87CEEB', '#9370DB'],
        },
    ],
};

const options = {
    plugins: {
        legend: {
            display: false,
        },
    },
    maintainAspectRatio: false,
};

const workouts = [
    { name: 'Strength', daysAgo: 1 },
    { name: 'Basketball', daysAgo: 3 },
    { name: 'Recovery', daysAgo: 6 },
];

const Chart = () => {
    return (
        <div className="chart-container">
            <h2>Chart</h2>
            <h3>Next week</h3>
            <div className="pie-chart">
                <Pie data={data} options={options} />
            </div>
            <div className="workout-info">
                <h3>Time since last workout</h3>
                <table>
                    <tbody>
                    {workouts.map((workout, index) => (
                        <tr key={index}>
                            <td>{workout.name}</td>
                            <td>{workout.daysAgo} day{workout.daysAgo > 1 ? 's' : ''}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default Chart;
