import React, { useContext, useEffect, useState, useRef } from 'react';
import { AuthContext } from "../context/AuthContext";
import { parseISO, addDays, startOfDay, endOfDay, format } from 'date-fns';

const Home = () => {
    const { fetchCalendarEntries, addCalendarEntry, updateCalendarEntry, deleteCalendarEntry } = useContext(AuthContext);
    const [entries, setEntries] = useState([]);
    const [newEntry, setNewEntry] = useState({ title: '', description: '', date: '', category: '' });
    const [editingEntry, setEditingEntry] = useState(null);
    const [showPanel, setShowPanel] = useState(false);
    const entryRefs = useRef({});

    // Configurable number of days forward
    const daysForward = 6; // Adjusted to cover exactly one week

    useEffect(() => {
        const getEntries = async () => {
            const fetchedEntries = await fetchCalendarEntries();
            setEntries(fetchedEntries);
        };

        getEntries();
    }, [fetchCalendarEntries]);

    const handleAddEntry = async (e) => {
        e.preventDefault();
        const addedEntry = await addCalendarEntry(newEntry);
        setEntries([...entries, addedEntry]);
        setNewEntry({ title: '', description: '', date: '', category: '' });
        cancel();
    };

    const handleUpdateEntry = async (e) => {
        e.preventDefault();
        const updated = await updateCalendarEntry(editingEntry._id, editingEntry);
        setEntries(entries.map(entry => (entry._id === editingEntry._id ? updated : entry)));
        setEditingEntry(null);
    };

    const handleDeleteEntry = async (id) => {
        await deleteCalendarEntry(id);
        setEntries(entries.filter(entry => entry._id !== id));
    };

    const handleEditEntry = (entry) => {
        setEditingEntry(entry);
    };

    const cancel = () => {
        const panel = document.querySelector('.background');
        panel.classList.remove('show');
        const addSessionBackground = document.querySelector('.addSessionBackground');
        addSessionBackground.classList.remove('show');

        // empty the form
        setNewEntry({ title: '', description: '', date: '', category: '' });
        setEditingEntry(null);
    };

    const onDragStart = (e, entry) => {
        const element = e.currentTarget;
        setTimeout(() => element.classList.add("dragging"), 0);
        console.log('dragging', element);
    };

    const onDrop = (e, entry) => {
        e.preventDefault();
        const droppedElement = entryRefs.current[entry._id];
        const parentElement = droppedElement ? droppedElement.parentElement : null;
        console.log('Dropped element:', droppedElement);
        console.log('Parent element:', parentElement);

        const droppedDayContainer = droppedElement.closest('.dayContainer');
        console.log('droppedDayContainer', droppedDayContainer);

        const startingDayContainer = document.querySelector('.dragging').closest('.dayContainer');
        console.log('startingDayContainer', startingDayContainer);

        // Remove starting day from its container and place it in the dropped day container
        startingDayContainer.removeChild(document.querySelector('.dragging'));
        droppedDayContainer.appendChild(document.querySelector('.dragging'));

        // Remove dragging class
        document.querySelector('.dragging').classList.remove('dragging');
    };

    const today = startOfDay(new Date()); // Ensure we start from the beginning of today
    const endDate = endOfDay(addDays(today, daysForward)); // Calculate the end date based on daysForward

    const filteredEntries = entries.filter(entry => {
        const entryDate = parseISO(entry.date); // Assuming entry.date is an ISO string
        return entryDate >= today && entryDate <= endDate;
    });

    const generateWeekDays = () => {
        const weekDays = [];
        for (let i = 0; i <= daysForward; i++) {
            const day = addDays(today, i);
            weekDays.push({
                date: day,
                label: format(day, 'EEEE')
            });
        }
        return weekDays;
    };

    const weekDays = generateWeekDays();

    const groupedEntries = weekDays.map(day => ({
        ...day,
        entries: filteredEntries.filter(entry => {
            const entryDate = parseISO(entry.date);
            return entryDate.toDateString() === day.date.toDateString();
        })
    })).filter(day => day.entries.length > 0);

    return (
        <div>
            <div className="eventsContainer">
                {groupedEntries.length > 0 ? groupedEntries.map(day => (
                    <div key={day.date.toISOString()} className="dayContainer hasEntries" id={day.date.toISOString()}>
                        <h2 className="dayHeader dayTitleClass" id="day.label">{day.label}</h2>
                        {day.entries.map(entry => (
                            <div
                                key={entry._id}
                                className="eventContainer"
                                draggable
                                ref={(el) => (entryRefs.current[entry._id] = el)}
                                onDragStart={(e) => onDragStart(e, entry)}
                                onDrop={(e) => onDrop(e, entry)}
                                onDragOver={(e) => e.preventDefault()} // Needed to allow dropping
                            >
                                <h2 className="eventHeaderTitle">{entry.title}</h2>
                                <p className="eventDescription">{entry.description}</p>
                                <button className="deleteButton" onClick={() => handleDeleteEntry(entry._id)}>Delete</button>
                                <button className="editButton" onClick={() => handleEditEntry(entry)}>Edit</button>
                            </div>
                        ))}
                    </div>
                )) : (
                    <p>No events for the upcoming week.</p>
                )}
            </div>
            <div className="addSessionBackground">
                <form className="form" onSubmit={editingEntry ? handleUpdateEntry : handleAddEntry}>
                    <div className="topButtons">
                        <button type="button" className="cancelButton" onClick={() => cancel()}>Cancel</button>
                        <p className="newSessionText">New Session</p>
                        <button className="submitButton" type="submit">{editingEntry ? 'Update' : 'Add'}</button>
                    </div>
                    <input
                        type="text"
                        placeholder="Title"
                        value={editingEntry ? editingEntry.title : newEntry.title}
                        onChange={(e) => editingEntry
                            ? setEditingEntry({ ...editingEntry, title: e.target.value })
                            : setNewEntry({ ...newEntry, title: e.target.value })
                        }
                        required
                    />
                    <input
                        type="text"
                        placeholder="Description"
                        value={editingEntry ? editingEntry.description : newEntry.description}
                        onChange={(e) => editingEntry
                            ? setEditingEntry({ ...editingEntry, description: e.target.value })
                            : setNewEntry({ ...newEntry, description: e.target.value })
                        }
                    />
                    <div className="date-input-container">
                        <input
                            type="date"
                            className="date-input"
                            value={editingEntry ? editingEntry.date : newEntry.date}
                            onChange={(e) => editingEntry
                                ? setEditingEntry({ ...editingEntry, date: e.target.value })
                                : setNewEntry({ ...newEntry, date: e.target.value })
                            }
                            required
                        />
                    </div>
                    {editingEntry && (
                        <button type="button" onClick={() => setEditingEntry(null)}>Cancel</button>
                    )}
                </form>
            </div>
        </div>
    );
};

export default Home;
