import React from 'react';
import { Pie } from 'react-chartjs-2';
import 'chart.js/auto';

const Chart = () => {
    // Sample data, replace this with your actual data fetching logic
    const activities = [
        { activity: "Basket", percentage: 50, color: "#FFA500", lastPerformed: "2 days ago" },
        { activity: "Styrketräning", percentage: 25, color: "#800080", lastPerformed: "1 day ago" },
        { activity: "Recovery", percentage: 25, color: "#0000FF", lastPerformed: "6 days ago" }
    ];

    const data = {
        labels: activities.map(a => a.activity),
        datasets: [
            {
                data: activities.map(a => a.percentage),
                backgroundColor: activities.map(a => a.color),
                hoverOffset: 4
            }
        ]
    };

    const options = {
        plugins: {
            legend: {
                position: 'bottom'
            }
        }
    };

    return (
        <div>
            <h1>Workoutsplit</h1>
            {activities.length > 0 ? (
                <>
                    <Pie data={data} options={options} />
                    <div>
                        <h2>Time since latest workout</h2>
                        <ul>
                            {activities.map((activity, index) => (
                                <li key={index}>{activity.activity}: {activity.lastPerformed}</li>
                            ))}
                        </ul>
                    </div>
                </>
            ) : (
                <p>No practices logged last week</p>
            )}
        </div>
    );
};

export default Chart;
